using System;using System.Collections.Generic;using System.Linq;using System.Web;using System.Net.Mail;using System.Net.Configuration; public class Email { public static void enviaEmail(String dest, String pessoa, String assunto, String cont) { string remetente = "suporte.neonae@gmail.com"; MailMessage email = new MailMessage(); email.To.Add(dest); email.From=new MailAddress(remetente,pessoa,System.Text.Encoding.UTF8); email.Subject = assunto; email.SubjectEncoding = System.Text.Encoding.UTF8; email.Body = cont; email.BodyEncoding = System.Text.Encoding.UTF8; email.IsBodyHtml = true; email.Priority = MailPriority.High; SmtpClient cliente = new SmtpClient(); cliente.Credentials = new System.Net.NetworkCredential(remetente, "tccboladao123"); cliente.Port = 587; cliente.Host = "smtp.gmail.com"; cliente.EnableSsl = true; cliente.Send(email); } public static void senhaEsq(String destin, String cli, String email,String senha) { enviaEmail(destin,"NEON AE - Anúncios de Eventos","NEON AE - Esqueceu a Senha", "<div style='border:solid lightgray 2px;padding-bottom:10px;padding-left:10px;height:150px;width:500px;'>" + "<div style='float:left;'><img src='https://lh3.googleusercontent.com/SH5iPar4KcJUF7ZdcZWtZdXNTDiSL42szpibbulkQasE_Euweq20Q7Tx169Ky7mroV-uoSSn=w1440-h900-rw-no' style='height: 150px; width: 170px;'/></div>" + "<div style=overflow:hidden;'><h3 style='height:0px; color:#000'>Prezado(a) " + "<b>"+cli+"</b>,</h3><br>"+ "<p>Login: "+email+"<br>Senha: "+senha+" - <b>Caso queira alterá-la, faça o Login e altere sua senha.</b></p>"+ "<p>Atenciosamente, Equipe NEON AE.</p></div></div>"); } public static void cad(String destin, String nome, String login, String senha) { enviaEmail(destin, "NEON AE - Anúncios de Eventos", "NEON AE - Bem-Vindo!!!", "<div style='border:solid lightgray 2px;width:500px;padding-bottom:10px;padding-left:10px;height:150px;'>" + "<div style='float:left;'><img src='https://lh3.googleusercontent.com/SH5iPar4KcJUF7ZdcZWtZdXNTDiSL42szpibbulkQasE_Euweq20Q7Tx169Ky7mroV-uoSSn=w1440-h900-rw-no' style='height: 150px; width: 170px;'/></div>" + "<div style=overflow:hidden;'><h3 style='height:0px; color:#000'>Prezado(a) " + "<b>" + nome + "</b>,</h3><br>" + "<p>Login: " + login + "<br>Senha: " + senha + "</p>"+ "<p>Atenciosamente, Equipe NEON AE.</p></div></div>"); } public static void alteraPerfil(String destin, String nome, String login, String senha) { enviaEmail(destin, "NEON AE - Anúncios de Eventos", "NEON AE - Alteração na Conta", "<div style='border:solid lightgray 2px;width:500px;padding-bottom:10px;padding-left:10px;height:150px;'>" + "<div style='float:left;'><img src='https://lh3.googleusercontent.com/SH5iPar4KcJUF7ZdcZWtZdXNTDiSL42szpibbulkQasE_Euweq20Q7Tx169Ky7mroV-uoSSn=w1440-h900-rw-no' style='height: 150px; width: 170px;'/></div>" + "<div style=overflow:hidden;'><h3 style='height:0px; color:#000'>Prezado(a) " + "<b>" + nome + "</b>,</h3><br>" + "<p>Login: " + login + "<br>Senha: " + senha + "</p>" + "<p>Atenciosamente, Equipe NEON AE.</p></div></div>"); } public static void compraIngresso(String destin, String nome, String qtd, String evento, String preco) { enviaEmail(destin, "NEON AE - Anúncios de Eventos", "NEON AE - Confirmação da Compra", "<div style='border:solid lightgray 2px;width:500px;padding-bottom:10px;padding-left:10px;height:150px;'>" + "<div style='float:left;'><img src='https://lh3.googleusercontent.com/SH5iPar4KcJUF7ZdcZWtZdXNTDiSL42szpibbulkQasE_Euweq20Q7Tx169Ky7mroV-uoSSn=w1440-h900-rw-no' style='height: 150px; width: 170px;'/></div>" + "<div style=overflow:hidden;'><h3 style='height:0px; color:#000'>Prezado(a) " + "<b>" + nome + "</b>,</h3><br>" + "<p>O senhor(a) realizou a compra de "+qtd+" ingresso(s) para o evento "+evento+".</p>"+ "<p>O custo total é de R$"+preco+".</p>" + "<p>Agradecemos e esperamos que o senhor(a) se divirta. Atenciosamente, equipe NEON AE.</p></div></div>"); } public static void erro(String tela) { enviaEmail("suporte.neonae@gmail.com", "NEON AE - Anúncios de Eventos", "NEON AE - ERRO NO SITE", "<div style='border:solid lightgray 2px;width:500px;padding-bottom:10px;padding-left:10px;height:150px;'>" + "<div style='float:left;'><img src='https://lh3.googleusercontent.com/SH5iPar4KcJUF7ZdcZWtZdXNTDiSL42szpibbulkQasE_Euweq20Q7Tx169Ky7mroV-uoSSn=w1440-h900-rw-no' style='height:150px;width:170px;text-align:center;'></div>" + "<div style=overflow:hidden;'><h3 style='height:0px;color:#000;padding-top:50px;'>Cauan, deu ruim na tela: " + tela+". Verificar e arrumar o código da mesma.</h3></div></div>"); } public static void desativaPerfil(String email, String user) { enviaEmail(email, "NEON AE - Anúncios de Eventos", "NEON AE - Desativação da Conta", "<div style='border:solid lightgray 2px;width:500px;padding-bottom:10px;padding-left:10px;height:150px;'>" + "<div style='float:left;'><img src='https://lh3.googleusercontent.com/SH5iPar4KcJUF7ZdcZWtZdXNTDiSL42szpibbulkQasE_Euweq20Q7Tx169Ky7mroV-uoSSn=w1440-h900-rw-no' style='height:150px;width:170px;text-align:center;'></div>" + "<div style=overflow:hidden;'><h3 style='height:0px;color:#000;padding-top:25px;'>"+user+", o Senhor(a) desativou sua conta.</h3>"+ "<br><br><b>Caso queira reativá-la, basta somente fazer o Login em sua conta.</b></div></div>"); } public static void welcomeBack(String nome,String email) { enviaEmail(email, "NEON AE - Anúncios de Eventos", "NEON AE - Bem-vindo de volta!!!", "<div style='border:solid lightgray 2px;width:500px;padding-bottom:10px;padding-left:10px;height:150px;'>" + "<div style='float:left;'><img src='https://lh3.googleusercontent.com/SH5iPar4KcJUF7ZdcZWtZdXNTDiSL42szpibbulkQasE_Euweq20Q7Tx169Ky7mroV-uoSSn=w1440-h900-rw-no' style='height:150px;width:170px;text-align:center;'></div>" + "<div style=overflow:hidden;'><h3 style='height:0px;color:#000;padding-top:50px;'>Obrigado e bem-vindo de volta, Senhor(a) "+nome+".</h3></div></div>"); } } 